import { useState } from "react";
import axios from "axios";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import { Card, CardGroup, CardImg, CardTitle, Col, Container, Row } from "react-bootstrap";

import '../css/cards.css'

export interface ACard {
    title: {
        english: string
        romaji: string
        native: string
    };
    coverImage: {
        large: string
    };
    seasonYear: number;
}

function SearchAnime() {
    const location = useLocation();
    const animes = location.state?.data;
    const navigate = useNavigate()

    const [values, setFormData] = useState({
        searchString: ''
    });

    const handleChange = (e: { target: { name: any; value: any; }; }) => {
        const { name, value } = e.target
        setFormData({ ...values, [name]: value });
    };

    const handleSubmit = (e: { preventDefault: () => void; }) => {
        e.preventDefault();
        const params = {
            searchString: values.searchString
        }
        axios.get('http://localhost:8080/api/anime/search', { params })
            .then(response => {
                const data: ACard[] = response.data.data.Page.media;
                navigate("/searchAnime", {state: {data: data}});
            })
    };

    return (
        <div>
            <div className="pageHeader">
                <h1>Search Anime</h1>
                <form onSubmit={handleSubmit}>
                    <input type='text'
                        name='searchString'
                        value={values.searchString}
                        onChange={handleChange}
                        placeholder='Search anime' />
                </form>
            </div>
            <div>
                    {animes.map((anime: ACard, index : number) => (
                            <Card className="card-grid" key={index}>
                                <Card.Img variant="top" src={anime.coverImage.large}/>
                                <Card.Title>{anime.title.english}</Card.Title>
                            </Card>
                    ))}
            </div>
        </div>
    )
}

export default SearchAnime;
