import { useState } from "react";
import "../css/pages.css"
import axios from "axios";
import { AnimeCard } from "../components/AnimeCard";

export interface ACard {
    title: string;
    image: string;
    year: number;
}

function Anime(animes: ACard[]) {

    const [values, setFormData] = useState({
        searchString: ''
    });

    const handleChange = (e: { target: { name: any; value: any; }; }) => {
        const { name, value } = e.target
        setFormData({ ...values, [name]: value });
    };

    const handleSubmit = (e: { preventDefault: () => void; }) => {
        e.preventDefault();
        const params = {
            searchString: values.searchString
        }
        axios.get('http://localhost:8080/api/anime/search', { params })
            .then(response => {
                const data: ACard[] = response.data.Page.Media;
                setAnimes(data)
            })
    };

    return (
        <div>
            <div>
                <h1>Anime</h1>
            </div>
            <div>
                <form onSubmit={handleSubmit}>
                    <input type='text'
                        name='searchString'
                        value={values.searchString}
                        onChange={handleChange}
                        placeholder='Search anime' />
                </form>
            </div>
            <div>
                {animes ? <AnimeCard title={animes[0].title} image={animes[0].image} year={animes[0].year} /> : <div></div>}
            </div>
        </div>
    )
}

export default Anime;
